    def score(self,textField,score):
        textboxValue = textField.toPlainText()
        x = textboxValue.split()
        if len(x) > 0:
            if x[-1] == "ok":
                try:
                    if score == 0:
                        self.score1+= int(x[0])
                        textField.setText(str(self.score1) +" poeng")
                    elif score == 1:
                        self.score2+= int(x[0])
                        textField.setText(str(self.score2) +" poeng")
                except:
                    pass
            elif  x[-1] == "clear":
                textField.clear()
                if score == 0:
                    self.score1=0
                    textField.setText(str(self.score1) +" poeng")
                elif score == 1:
                    self.score2=0
                    textField.setText(str(self.score2) +" poeng")
                    
                
            


